(function(x){

	var util = require('one.util'),
		initJson = util.session['ini'];
	if (initJson) {
		x=JSON.parse(initJson);
		return;
	};
	if (!util.fs.existsSync(util.initFile)) {
		x={
			"language":"zh_CN"
		};
		util.fs.mkdirsSync(util.dataRoot);
		util.fs.writeJsonSync(util.initFile,x);
		util.session['ini']=JSON.stringify(x);
		return;
	};

	x = util.fs.readJsonSync(util.initFile);

})(exports);