/**
 * utility module
 * @module Common
 * @class util
 * @static
 */
(function(x,$){
	
	x.fs = require('fs-extra');
	x.userName = process.env['USERNAME'];
	x.localdb = window.localStorage||{};
	x.session = window.sessionStorage||{};
	x.appRoot = process.execPath.substr(0,process.execPath.lastIndexOf('\\')+1);

	//package json
	if ( !(x.package=x.session['package']) ) {
		x.package = x.fs.readJsonSync('package.json');
		x.session['package'] = JSON.stringify(x.package);
	}else{
		x.package = JSON.parse(x.package);
	};

	x.dataRoot = x.appRoot+"data\\$\\".replace('$',x.package.name);
	x.initFile = x.dataRoot+"app.ini";
	x.$win = $(window);

})(exports,global.$);